import React, { useMemo } from 'react'
import { MapContainer, Marker, TileLayer } from 'react-leaflet';
import RoutePolyline from './RoutePolyline'
import ClickedLocationPopup from './RoutePolyline'
import styles from '../styles.module.css'
import 'leaflet/dist/leaflet.css';

function RenderMap({ currentPosition, activePath, kilometers })
{
    /*
    const activePathDependency = useMemo(() => {
        return activePath;
    }, [activePath]);

    const kilometersDependency = useMemo(() => {
        return kilometers;
    }, [kilometers]);

    //needed to prevent missing dependencies with useMemo for the polyline
    const polyline = useMemo(
        () => (
            <RoutePolyline path={activePathDependency} kilometers={kilometersDependency}/>
        ),
        [activePathDependency, kilometersDependency]
    )
    */
    //polyline is only re rendered when active path or center point changes, so the map will not
    //move when I type in the textboxes

    return (
        <MapContainer className = {styles.map} center={currentPosition} zoom={15}>
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            <RoutePolyline path={activePathDependency} kilometers={kilometersDependency}/>
        </MapContainer>
    );
    //needs to be inside a function so I can use useMemo hook
}

export default RenderMap;
