import React, { useState } from "react";

export default InputReader = () => {
    const[data, setData] = useState([{}]);
    state = {
        "textBoxParams": {} // to keep track of the text box values
    }

    handleInputChange = (e, key) => {
        this.setState(prevState => ({
            "textBoxValues": {
                ...prevState.textBoxValues,
                [key]: e.target.value
            }
        }))
    }

    function submitParams()
    {
        var params = state["textBoxValues"];
        fetch(`http://127.0.0.1:5000/data?dist_min=${params["min_dist"]}&dist_max=${params["max_dist"]}&loc_lat=${params["loc_lat"]}&loc_lon=${params["loc_lon"]}`, { method: 'get', mode: 'cors' }).then(
            res => res.json()
        ).then(
            data => {
                setData(data)
                console.log(data)
            }
        );

        console.log("Button Pressed");
    }

    return(
        <div>
            <input 
                type='number' 
                onChange={e => handleInputChange(e, "loc_lat")} //location lat
            />
            <input 
                type='number' 
                onChange={e => handleInputChange(e, "loc_lon")} //location lon
            />
            <input 
                type='number' 
                onChange={e => handleInputChange(e, "min_dist")} //min distance
            />
            <input 
                type='number' 
                onChange={e => handleInputChange(e, "max_dist")} //max distance
            />
        </div>
    );
}
