import React, { Component, useState } from "react";

function InputReader() {

    const[data, setData] = useState([{}]);
    var textBoxParams = {};

    function handleInput(e, key){
        /*
        this.setState(prevState => ({
            "textBoxValues": {
                ...prevState.textBoxValues,
                [key]: e.target.value
            }
        }))
        */
        textBoxParams[key] = e.target.value;
        console.log(textBoxParams)
    }

    function submitParams()
    {
        fetch(`http://127.0.0.1:5000/data?dist_min=${textBoxParams["min_dist"]}&dist_max=${textBoxParams["max_dist"]}&loc_lat=${textBoxParams["loc_lat"]}&loc_lon=${textBoxParams["loc_lon"]}`, { method: 'get', mode: 'cors' }).then(
            res => res.json()
        ).then(
            data => {
                setData(data)
                console.log(data)
            }
        );

        console.log("Button Pressed");
    }

    return(
        <div>
            <button onClick={submitParams}>
                Get The Loops
            </button>
            <input 
                type='number' 
                onChange={e => handleInput(e, "loc_lat")} //location lat
            />
            <input 
                type='number' 
                onChange={e => handleInput(e, "loc_lon")} //location lon
            />
            <input 
                type='number' 
                onChange={e => handleInput(e, "min_dist")} //min distance
            />
            <input 
                type='number' 
                onChange={e => handleInput(e, "max_dist")} //max distance
            />
        </div>
    );
}

export default InputReader;
