import React, { useState, useEffect } from "react";
//import { Home } from './containers/Home';

function App() {
    const[data, setdata] = useState({
        name:"",
        date:""
    });

    useEffect(() => {
    fetch("/data").then((res) =>
            res.json().then((data) => {
                setdata({
                    name: data.name,
                    date: data.date,
                });
            })
        );
    }, []);

  return (
    <div className="App">
        <header className="App-header">
            <h1>React and flask</h1>
            {/* Calling a data from setdata for showing */}
            <p>{data.name}</p>
            <p>{data.date}</p>
        </header>
    </div>
  );
}

export default App;
