import React, { Component } from "react";
import MapRenderer from './MapRenderer'
import styles from '../styles.module.css'
import 'leaflet/dist/leaflet.css'

class InputReader extends Component {

    state = 
    {
        data: {},
        textBoxValues: {},
        currentPosition: {},
        usedPosition: {},
        displayedPathIndex: -1,
        kilometers: 0,
        loading: false,
        requestError: ""
    }

    markerCallback = (position) => {
        let roundedPosition = {lat: Math.round(position.lat * 1000000) / 1000000, lng: Math.round(position.lng * 1000000) / 1000000};
        this.setState({usedPosition: roundedPosition});
    }

    handleInputChange = (e, key) => {
        let copyParams = { ...this.state.textBoxValues, [key]: e.target.value};
        this.setState({textBoxValues: copyParams});

        if(this.state.currentPosition.lat === undefined) //assumes you won't change loc before reloading page
        {
            if(navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(this.success, this.error);
            } else {
                console.log("Could not use geolocation");
            }
        }
    }

    handleSubmit = () =>
    {
        if(this.state.usedPosition.lat === undefined || this.state.usedPosition.lng === undefined) {
            this.setState({usedPosition: this.state.currentPosition}, () => {
                this.getLoops()
            });
        } else {
            this.getLoops();
        }

        console.log("Button Pressed");
    }

    cyclePaths = (change) =>
    {
        let new_index = this.state.displayedPathIndex + change;
        if(this.state.displayedPathIndex !== -1) {
            if(new_index >= 0 && new_index < this.state.data.loops.length) {
                this.setState({displayedPathIndex: new_index});
            }
        }
    }

    setDistanceMode = (km) =>
    {
        this.setState({kilometers: km});
    }

    getLoops() {
        this.setState({loading: true});

        fetch(`http://127.0.0.1:5000/data?dist_min=${this.state.textBoxValues.min_dist}&dist_max=${this.state.textBoxValues.max_dist}&loc_lat=${this.state.usedPosition.lat}&loc_lon=${this.state.usedPosition.lng}&km=${this.state.kilometers}`, { method: 'get', mode: 'cors' })
        .then(
            res => res.json()
        ).then(
            data => {
                this.setState({data: data});
                this.setState({loading: false});
                this.setState({requestError: ""})
                if(data.loops.length > 0) {
                    this.setState({displayedPathIndex: 0});

                } else {
                    this.setState({requestError: "No routes were found. Try adjusting your distances or your starting location."})
                    this.setState({data:{}})
                    console.log("No loops were found");
                }
                console.log(data);
            }
        ).catch((error) => {
            console.log(error);
            this.setState({loading: false});
            this.setState({requestError: "The request failed. Adjust your parameters and try again, or check console for error message."});
        });
    }

    success = (position) => {
        const lat = position.coords.latitude;
        const lon = position.coords.longitude;

        this.setState({currentPosition: {lat:lat, lng:lon}});
    }

    error() {
        console.log("Could not get location");
    }

    MapDisplay = () => {
        //const activePath = this.state.displayedPathIndex !== -1 ? this.state.data.loops[this.state.displayedPathIndex].path : [];
        const activePath = this.state.data.loops !== undefined ? this.state.data.loops[this.state.displayedPathIndex] : {path: [], center: [], distance: 0.0}

        if(this.state.currentPosition.lat === undefined) { //await current position
            navigator.geolocation.getCurrentPosition(this.success, this.error);
            return (
                <div></div>
            );
        } else {
            return (
                <div>
                    <MapRenderer currentPosition={this.state.currentPosition} activePath={activePath} kilometers={this.state.kilometers} markerCallback={this.markerCallback}/>
                </div>
            );
        }
    }

    render(){

        const errorMessage = this.state.requestError === "" ? (<div></div>) : (<p className={styles.error}>{this.state.requestError}</p>);

        return(
            <div>
                <this.MapDisplay/>
                {errorMessage}
                <div className={styles.container}>
                    <button className={styles.button} onClick={this.handleSubmit} disabled={this.state.loading}>
                        FIND ROUTES
                    </button>
                    <input
                        className={styles.input + " " + styles.short}
                        placeholder="min dist"
                        type='number' 
                        onChange={e => this.handleInputChange(e, "min_dist")} //min distance
                    />
                    <input
                        className={styles.input + " " + styles.short}
                        placeholder="max dist"
                        type='number' 
                        onChange={e => this.handleInputChange(e, "max_dist")} //max distance
                    />
                    <button className={styles.button} onClick={() => this.cyclePaths(-1)} disabled={this.state.displayedPathIndex !== -1 ? this.state.displayedPathIndex === 0 : true}>PREV</button>
                    <button className={styles.button} onClick={() => this.cyclePaths(1)} disabled={this.state.displayedPathIndex !== -1 ? this.state.displayedPathIndex === this.state.data.loops.length - 1 : true}>NEXT</button>

                    <button className={styles.button} onClick={() => this.setDistanceMode(1)} disabled={this.state.kilometers}>KM</button>
                    <button className={styles.button} onClick={() => this.setDistanceMode(0)} disabled={!this.state.kilometers}>MI</button>
                </div>
                <div className={styles.textBlock}>
                    <p className={styles.paragraph}>
                        runningroutes.com is a simple tool that uses the depth first search (DFS) algorithm with OpenStreetMap Data to find and display running routes in your area.
                        Click on the map to change the location of the marker, which will serve as the starting and ending point for all routes found. Specify the range of desired distances
                        with the two textboxes. Be aware that large distances or routes in very dense areas will take a long time to be calculated.
                    </p>
                </div>
            </div>
        )
    }
};

export default InputReader;
