import React, { Component } from "react";
import styles from '../styles.module.css'
import { MapContainer, TileLayer, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';


class InputReader extends Component {

    state = 
    {
        data: {},
        textBoxValues: {},
        currentPosition: [],
        displayedPath: []
    }

    handleInputChange = (e, key) => {
        let copyParams = { ...this.state.textBoxValues, [key]: e.target.value};
        this.setState({textBoxValues: copyParams});

        if(this.state.currentPosition.length === 0) //assumes you won't change loc before reloading page
        {
            if(navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(this.success, this.error);
            } else {
                console.log("Could not use geolocation");
            }
        }
    }

    handleSubmit = () =>
    {
        const params = this.state.textBoxValues;

        if(params["loc_lat"] === undefined || params["loc_lon"] === undefined) {
            const pos = this.state.currentPosition;
            let copyParams = { ...this.state.textBoxValues, loc_lat: pos[0], loc_lon: pos[1]};
            this.setState({textBoxValues: copyParams}, () => {
                this.getLoops()
            });
        } else {
            this.getLoops();
        }

        console.log("Button Pressed");
    }

    getLoops() {
        const params = this.state.textBoxValues;

        fetch(`http://127.0.0.1:5000/data?dist_min=${params["min_dist"]}&dist_max=${params["max_dist"]}&loc_lat=${params["loc_lat"]}&loc_lon=${params["loc_lon"]}`, { method: 'get', mode: 'cors' }).then(
            res => res.json()
        ).then(
            data => {
                this.setState({data: data});
                if(data.loops.length > 0) {
                    this.setState({displayedPath: data.loops[0].path});
                } else {
                    console.log("No loops were found");
                }
                console.log(data);
            }
        );
    }

    success = (position) => {
        const lat = position.coords.latitude;
        const lon = position.coords.longitude;

        this.setState({currentPosition: [lat, lon]});
    }

    error() {
        console.log("Could not get location");
    }

    MapDisplay = () => {
        const activePath = this.state.displayedPath;
        const currentPosition = activePath.length > 0 ? activePath[0] : this.state.currentPosition;
        const lineColor = { color: 'red' }

        if(currentPosition.length === 0) { //await current position
            navigator.geolocation.getCurrentPosition(this.success, this.error);
            return (
                <div></div>
            );
        } else {
            return (
                <MapContainer className = {styles.map} center={currentPosition} zoom={15}>
                    <TileLayer
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    />
                    <Polyline pathOptions={lineColor} positions={activePath} />
                </MapContainer>
            );
        }
    }

    render(){
        return(
            <div>
                <this.MapDisplay/>
                <div className={styles.container}>
                    <button className={styles.button} onClick={this.handleSubmit}>
                        FIND ROUTES
                    </button>
                    <input
                        className={styles.input}
                        type='number'
                        onChange={e => this.handleInputChange(e, "loc_lat")} //location lat
                    />
                    <input
                        className={styles.input}
                        type='number' 
                        onChange={e => this.handleInputChange(e, "loc_lon")} //location lon
                    />
                    <input
                        className={styles.input}
                        type='number' 
                        onChange={e => this.handleInputChange(e, "min_dist")} //min distance
                    />
                    <input
                        className={styles.input}
                        type='number' 
                        onChange={e => this.handleInputChange(e, "max_dist")} //max distance
                    />
                </div>
            </div>
        )
    }
};

export default InputReader;
