import React, { Component, useMemo } from "react";
import styles from '../styles.module.css'
import { MapContainer, TileLayer, Polyline, Popup, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

function findEstimatedTime(paceInSeconds, distance)
{
    let totalTime = Math.floor(distance * paceInSeconds);
    let hours = Math.floor(totalTime / 3600);
    let minutes = Math.floor((totalTime - hours * 3600) / 60)
    let seconds = totalTime - hours * 3600 - minutes * 60;

    if(seconds < 10){
        seconds = `0${seconds}`
    }
    if(minutes < 10 && hours >= 1){
        minutes = `0${minutes}`
    }


    if(hours === 0) {
        return `${minutes}:${seconds}`;
    } else {
        return `${hours}:${minutes}:${seconds}`;
    }
}

function RenderPolyline({ path, kilometers })
{
    const map = useMap();
    const lineColor = { color: 'red' };
    let dist = path.distance;

    let t1, t2, t3, eg = "";
    if(kilometers){
        dist = dist * 1.609;
        t1 = `Time at 4:45/kilometer: ${findEstimatedTime(60 * 4.75, dist)}`;
        t2 = `Time at 4:15/kilometer: ${findEstimatedTime(60 * 4.25, dist)}`;
        t3 = `Time at 3:45/kilometer: ${findEstimatedTime(60 * 3.75, dist)}`;
        eg = `Elevation Gain: ${path.elevation_gain} meters`;
    } else {
        t1 = `Time at 8:00/mile: ${findEstimatedTime(60 * 8, dist)}`;
        t2 = `Time at 7:30/mile: ${findEstimatedTime(60 * 7.5, dist)}`;
        t3 = `Time at 7:00/mile: ${findEstimatedTime(60 * 7, dist)}`;
        eg = `Elevation Gain: ${Math.floor(path.elevation_gain * 3.28)} feet`;
    }

    if(path.path.length > 0)
    {
        map.setView(path.center, 15);
    }

    return(
        <Polyline pathOptions={lineColor} positions={path.path}>
            <Popup>
                {`Distance: ${Math.round(dist * 100.0) / 100.0} ${kilometers ? "kilometers" : "miles"}`}<br/>
                {t1}<br/>
                {t2}<br/>
                {t3}<br/>
                {eg}
            </Popup>
        </Polyline>
    );
}

function RenderMap({ currentPosition, activePath, kilometers })
{
    const activePathDependency = useMemo(() => {
        return activePath;
    }, [activePath]);

    const kilometersDependency = useMemo(() => {
        return kilometers;
    }, [kilometers]);

    //needed to prevent missing dependencies with useMemo for the polyline

    const polyline = useMemo(
        () => (
            <RenderPolyline path={activePathDependency} kilometers={kilometersDependency}/>
        ),
        [activePathDependency, kilometersDependency]
    ) 
    //polyline is only re rendered when active path or center point changes, so the map will not
    //move when I type in the textboxes

    return (
        <MapContainer className = {styles.map} center={currentPosition} zoom={15}>
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            {polyline}
        </MapContainer>
    );
    //needs to be inside a function so I can use useMemo hook
}

class InputReader extends Component {

    state = 
    {
        data: {},
        textBoxValues: {},
        currentPosition: [],
        displayedPathIndex: -1,
        kilometers: 0,
        loading: false
    }

    handleInputChange = (e, key) => {
        let copyParams = { ...this.state.textBoxValues, [key]: e.target.value};
        this.setState({textBoxValues: copyParams});

        if(this.state.currentPosition.length === 0) //assumes you won't change loc before reloading page
        {
            if(navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(this.success, this.error);
            } else {
                console.log("Could not use geolocation");
            }
        }
    }

    handleSubmit = () =>
    {
        const params = this.state.textBoxValues;

        if(params.loc_lat === undefined || params.loc_lon === undefined || params.loc_lat === "" || params.loc_lon === "") {
            const pos = this.state.currentPosition;
            let copyParams = { ...this.state.textBoxValues, loc_lat: pos[0], loc_lon: pos[1]};
            this.setState({textBoxValues: copyParams}, () => {
                this.getLoops()
            });
        } else {
            this.getLoops();
        }

        console.log("Button Pressed");
    }

    cyclePaths = (change) =>
    {
        let new_index = this.state.displayedPathIndex + change;
        if(this.state.displayedPathIndex !== -1) {
            if(new_index >= 0 && new_index < this.state.data.loops.length) {
                this.setState({displayedPathIndex: new_index});
            }
        }
    }

    setDistanceMode = (km) =>
    {
        this.setState({kilometers: km});
    }

    getLoops() {
        const params = this.state.textBoxValues;
        this.setState({loading: true});

        fetch(`http://127.0.0.1:5000/data?dist_min=${params.min_dist}&dist_max=${params.max_dist}&loc_lat=${params.loc_lat}&loc_lon=${params.loc_lon}&km=${this.state.kilometers}`, { method: 'get', mode: 'cors' }).then(
            res => res.json()
        ).then(
            data => {
                this.setState({data: data});
                this.setState({loading: false});
                if(data.loops.length > 0) {
                    this.setState({displayedPathIndex: 0});
                } else {
                    console.log("No loops were found");
                }
                console.log(data);
            }
        );
    }

    success = (position) => {
        const lat = position.coords.latitude;
        const lon = position.coords.longitude;

        this.setState({currentPosition: [lat, lon]});
    }

    error() {
        console.log("Could not get location");
    }

    MapDisplay = () => {
        //const activePath = this.state.displayedPathIndex !== -1 ? this.state.data.loops[this.state.displayedPathIndex].path : [];
        const activePath = this.state.displayedPathIndex !== -1 ? this.state.data.loops[this.state.displayedPathIndex] : {path: [], center: [], distance: 0.0}
        const currentPosition = activePath.path.length > 0 ? activePath.path[0] : this.state.currentPosition;

        if(currentPosition.length === 0) { //await current position
            navigator.geolocation.getCurrentPosition(this.success, this.error);
            return (
                <div></div>
            );
        } else {
            return (
                <div>
                    <RenderMap currentPosition={currentPosition} activePath={activePath} kilometers={this.state.kilometers}/>
                </div>
            );
        }
    }

    render(){
        return(
            <div>
                <this.MapDisplay/>
                <div className={styles.container}>
                    <button className={styles.button} onClick={this.handleSubmit} disabled={this.state.loading}>
                        FIND ROUTES
                    </button>
                    <input
                        className={styles.input}
                        placeholder="latitude"
                        type='number'
                        onChange={e => this.handleInputChange(e, "loc_lat")} //location lat
                    />
                    <input
                        className={styles.input}
                        placeholder="longitude"
                        type='number' 
                        onChange={e => this.handleInputChange(e, "loc_lon")} //location lon
                    />
                    <input
                        className={styles.input + " " + styles.short}
                        placeholder="min dist"
                        type='number' 
                        onChange={e => this.handleInputChange(e, "min_dist")} //min distance
                    />
                    <input
                        className={styles.input + " " + styles.short}
                        placeholder="max dist"
                        type='number' 
                        onChange={e => this.handleInputChange(e, "max_dist")} //max distance
                    />
                    <button className={styles.button} onClick={() => this.cyclePaths(-1)}>PREV</button>
                    <button className={styles.button} onClick={() => this.cyclePaths(1)}>NEXT</button>

                    <button className={styles.button} onClick={() => this.setDistanceMode(1)} disabled={this.state.kilometers}>KM</button>
                    <button className={styles.button} onClick={() => this.setDistanceMode(0)} disabled={!this.state.kilometers}>MI</button>
                </div>
                <p className={styles.paragraph}>
                    runningroutes.com is a simple tool that uses the depth first search (DFS) algorithm with OpenStreetMap Data to find and display running routes in your area.<br/><br/>
                    Whether you are an experienced runner or a beginner, running the same routes over and over again can get boring. As I high school athlete,<br/>
                    I run almost every day, often repeating the exact same path as I did the day before. 
                </p>
            </div>
        )
    }
};

export default InputReader;
