import React from 'react'
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import RoutePolyline from './RoutePolyline'
import LocationMarker from './RoutePolyline'
import styles from '../styles.module.css'
import 'leaflet/dist/leaflet.css';

function RenderMap({ currentPosition, activePath, kilometers })
{
    return (
        <MapContainer className = {styles.map} center={currentPosition} zoom={15}>
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            <RoutePolyline path={activePath} kilometers={kilometers}/>
            <Marker position={ currentPosition }>
                <Popup>
                    The route's starting and ending point
                </Popup>
            </Marker>
        </MapContainer>
    );
    //needs to be inside a function so I can use useMemo hook
}

export default RenderMap;
